def translate(key, lang='fr'):
    """
    Traduit une clé dans la langue spécifiée
    
    Args:
        key: Clé de traduction (en anglais)
        lang: Code de langue (fr, en, es, it, pl)
    
    Returns:
        Texte traduit ou la clé si traduction non trouvée
    """
    if key in TRANSLATIONS:
        return TRANSLATIONS[key].get(lang, TRANSLATIONS[key].get('fr', key))
    return key


def get_language_name(code):
    """Retourne le nom de la langue en fonction du code"""
    names = {
        'fr': 'Français',
        'en': 'English',
        'es': 'Español',
        'it': 'Italiano',
        'pl': 'Polski',
    }
    return names.get(code, 'Français')
